export interface SessionCreateResponse {
  success: boolean;
  sessionName: string;
  error?: string;
}
export interface SessionActionResponse {
  success: boolean;
  error?: string;
}
export interface WindowsListResponse {
  windows: TmuxWindow[];
}
export interface WindowCreateResponse {
  success: boolean;
  window?: TmuxWindow;
  error?: string;
}

