      error: 'Window name cannot be empty' 
    });
  }

  try {
    // Use execAsync with proper escaping
    const target = `${sessionName}:${windowIndex}`;
    await execAsync(`tmux rename-window -t '${target}' '${newName.replace(/'/g, "'\\''")}'`);
    res.json({ success: true });
  } catch (error: any) {
