        if (this.mediaSource.readyState === 'open') {
          this.mediaSource.endOfStream()
        }
      } catch (e) {
        // Ignore errors during cleanup
      }
      this.mediaSource = null
    }
  }
}

// Export singleton instance
export const audioPlayer = new AudioPlayer()

