import { ref, computed } from 'vue'

// Global terminal state management
export const terminalStore = {
  currentSession: ref(null),
  currentWindow: ref(null),
  isAttached: ref(false),

  setSession(sessionName) {
    console.log('Terminal store: setting session to', sessionName)
    this.currentSession.value = sessionName
    this.isAttached.value = false
  },

  setWindow(windowIndex) {
    console.log('Terminal store: setting window to', windowIndex)
    this.currentWindow.value = windowIndex
  },

  setAttached(attached) {
    console.log('Terminal store: attached state', attached)
    this.isAttached.value = attached
  },

  reset() {
    this.currentSession.value = null
    this.currentWindow.value = null
    this.isAttached.value = false
  }
}

