    this.currentWindow.value = windowIndex
  },

  setAttached(attached) {
    console.log('Terminal store: attached state', attached)
    this.isAttached.value = attached
  },

  reset() {
    this.currentSession.value = null
    this.currentWindow.value = null
    this.isAttached.value = false
  }
}

