  const { name } = req.body;
  const sessionName = name || `session-${Date.now()}`;

  // First check if tmux server is running
  try {
    await execAsync('tmux list-sessions 2>/dev/null');
  } catch (error) {
    // TMUX server not running, start it with a dummy session that will be removed
    try {
