      setTimeout(() => listSessionWindows(ws, sessionName), 200);
    } else {
      console.error('Failed to select window, exit code:', code);
      ws.send(JSON.stringify({
        type: 'window-selected',
        success: false,
        error: 'Failed to select window'
      }));
    }
  });
}

