// Client-side output buffering for smoother rendering
const flushClientOutput = () => {
  if (clientOutputBuffer && terminal) {
    try {
      terminal.write(clientOutputBuffer)
      clientOutputBuffer = ''
    } catch (err) {
      console.warn('Error writing to terminal:', err)
    }
  }
