      }
    }
  }, 30000) // Ping every 30 seconds
}

stopPing() {
  if (this.pingInterval) {
    clearInterval(this.pingInterval)
    this.pingInterval = null
  }
}

close() {
  this.stopPing()
  if (this.ws) {
    this.ws.close()
  }
}

ensureConnected() {
  if (this.isConnected) {
    return Promise.resolve()
  }
  return this.connect()
}

