app.post('/api/sessions/:sessionName/windows/:windowIndex/select', (req, res) => {
  const { sessionName, windowIndex } = req.params;

  const selectCmd = spawn('tmux', ['select-window', '-t', `${sessionName}:${windowIndex}`]);

  selectCmd.on('close', (code) => {
