/* Mobile-specific utilities */
@media (max-width: 768px) {
  /* Hide elements on mobile */
  .mobile-hidden {
    display: none !important;
  }

  /* Mobile font sizes */
  .mobile-text-xs {
    font-size: 0.65rem;
  }

  /* Mobile padding adjustments */
  .mobile-p-2 {
    padding: 0.5rem;
  }

  /* Touch-friendly button sizes */
  .mobile-touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Mobile terminal adjustments */
  .xterm {
    padding: 8px;
    font-size: 12px;
  }

  /* Mobile scrollbar (thinner) */
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  /* Mobile overlay backdrop */
  .mobile-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    z-index: 40;
  }

  /* Mobile sidebar positioning */
  .mobile-sidebar {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    z-index: 50;
  }
}

