const express = require('express');
const https = require('https');
const fs = require('fs');
const WebSocket = require('ws');
const pty = require('node-pty');
const { spawn, exec } = require('child_process');
const path = require('path');
const cors = require('cors');
const os = require('os');
const { promisify } = require('util');

const execAsync = promisify(exec);

const app = express();
const port = 3000;
const httpsPort = 3443;

// HTTPS configuration
const httpsOptions = {
  key: fs.readFileSync(path.join(__dirname, 'certs', 'key.pem')),
  cert: fs.readFileSync(path.join(__dirname, 'certs', 'cert.pem'))
};

app.use(cors());
app.use(express.json());
app.use(express.static('public'));

// System stats endpoint
app.get('/api/stats', (req, res) => {
  const cpus = os.cpus();
  const totalMem = os.totalmem();
  const freeMem = os.freemem();
  const usedMem = totalMem - freeMem;
  const loadAvg = os.loadavg();

  res.json({
    cpu: {
      cores: cpus.length,
      model: cpus[0].model,
      usage: loadAvg[0],
      loadAvg: loadAvg
    },
    memory: {
      total: totalMem,
      used: usedMem,
      free: freeMem,
      percent: ((usedMem / totalMem) * 100).toFixed(1)
    },
    uptime: os.uptime(),
    hostname: os.hostname(),
    platform: os.platform(),
    arch: os.arch()
  });
});

// REST API endpoints
app.get('/api/sessions', async (req, res) => {
  // First check if tmux server is running
  try {
    await execAsync('tmux list-sessions 2>/dev/null');
  } catch (error) {
    // TMUX not running, return empty list
    res.json({ sessions: [] });
    return;
  }

  const listCmd = spawn('tmux', ['list-sessions', '-F', '#{session_name}:#{session_attached}:#{session_created}:#{session_windows}:#{session_width}x#{session_height}']);

  let output = '';

  listCmd.stdout.on('data', (data) => {
    output += data.toString();
  });

  listCmd.on('close', (code) => {
    if (code !== 0) {
      res.json({ sessions: [] });
      return;
    }

    const sessions = output.trim().split('\n').filter(line => line)
      .map(line => {
        const [name, attached, created, windows, dimensions] = line.split(':');
        return { 
          name, 
          attached: attached === '1', 
          created: new Date(parseInt(created) * 1000),
          windows: parseInt(windows),
          dimensions
        };
      });

    res.json({ sessions });
  });
});

app.post('/api/sessions/:name/kill', (req, res) => {
  const { name } = req.params;
