// Singleton WebSocket manager to ensure single connection
class WebSocketManager {
  constructor() {
    this.ws = null
    this.isConnected = false
    this.messageHandlers = new Map()
    this.connectionPromise = null
    this.pingInterval = null
    this.reconnectAttempts = 0
    this.maxReconnectAttempts = 5
  }

  connect() {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      return Promise.resolve()
    }

    if (this.connectionPromise) {
      return this.connectionPromise
    }

    this.connectionPromise = new Promise((resolve) => {
      // Always use the current host for WebSocket connections
      // This works for localhost, network IPs, and Tailscale IPs
      const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:'
      const wsUrl = import.meta.env.DEV 
        ? `${protocol}//${window.location.hostname}:3000/ws`
        : `${protocol}//${window.location.host}/ws`

      this.ws = new WebSocket(wsUrl)

      this.ws.onopen = () => {
        this.isConnected = true
        this.connectionPromise = null
        this.reconnectAttempts = 0
        console.log('WebSocket connected')

        // Start ping to keep connection alive
        this.startPing()

        resolve()
      }

      this.ws.onmessage = (event) => {
        const data = JSON.parse(event.data)
        const handlers = this.messageHandlers.get(data.type) || []
        handlers.forEach(handler => handler(data))
      }

      this.ws.onerror = (error) => {
        console.error('WebSocket error:', error)
      }

      this.ws.onclose = (event) => {
        console.log('WebSocket disconnected:', event.code, event.reason)
        this.isConnected = false
        this.ws = null
        this.connectionPromise = null
        this.stopPing()

        // Only reconnect if we haven't exceeded max attempts
        if (this.reconnectAttempts < this.maxReconnectAttempts) {
          this.reconnectAttempts++
          const delay = event.code === 1000 ? 3000 : 1000; // 1s for errors, 3s for normal close
          console.log(`Reconnect attempt ${this.reconnectAttempts}/${this.maxReconnectAttempts} in ${delay}ms`)
          setTimeout(() => this.connect(), delay)
        } else {
          console.error('Max reconnection attempts reached')
        }
      }
    })

    return this.connectionPromise
  }

  send(data) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      try {
        this.ws.send(JSON.stringify(data))
      } catch (err) {
        console.error('WebSocket send failed:', err)
        // Force reconnect on send failure
        this.connect()
      }
    } else {
      console.warn('WebSocket not connected, message not sent:', data)
      // Try to reconnect
      this.connect()
    }
  }

  onMessage(type, handler) {
    if (!this.messageHandlers.has(type)) {
      this.messageHandlers.set(type, [])
    }
    this.messageHandlers.get(type).push(handler)
  }

  offMessage(type, handler) {
    if (this.messageHandlers.has(type)) {
      const handlers = this.messageHandlers.get(type)
      const index = handlers.indexOf(handler)
      if (index > -1) {
        handlers.splice(index, 1)
      }
    }
  }

  startPing() {
    this.stopPing()
    this.pingInterval = setInterval(() => {
      if (this.ws && this.ws.readyState === WebSocket.OPEN) {
        try {
          this.ws.send(JSON.stringify({ type: 'ping' }))
        } catch (err) {
          console.warn('Ping failed:', err)
          this.connect() // Try to reconnect
        }
      }
    }, 30000) // Ping every 30 seconds
  }

  stopPing() {
    if (this.pingInterval) {
      clearInterval(this.pingInterval)
      this.pingInterval = null
    }
  }

  close() {
    this.stopPing()
    if (this.ws) {
      this.ws.close()
    }
  }

  ensureConnected() {
    if (this.isConnected) {
      return Promise.resolve()
    }
    return this.connect()
  }
}

// Export singleton instance
export const wsManager = new WebSocketManager()

