const handleTouchEnd = (e) => {
  const touchDuration = Date.now() - touchStartTime
  // Only focus if it's a quick tap, not a scroll
  if (touchDuration < 200) {
    focusTerminal()
  }
}

// Mobile keyboard control methods
const sendKey = (key) => {
  if (!terminal) return

  const keyMap = {
    'Escape': '\x1b',
    'Tab': '\t',
    'ArrowUp': '\x1b[A',
    'ArrowDown': '\x1b[B',
    'ArrowLeft': '\x1b[D',
    'ArrowRight': '\x1b[C',
  }

  const data = keyMap[key] || key
  if (ctrlPressed.value && key.length === 1) {
    // Convert to control character
    const code = key.toUpperCase().charCodeAt(0) - 64
    terminal.paste(String.fromCharCode(code))
  } else {
    terminal.paste(data)
  }

  terminal.focus()
}

const sendCtrlKey = (key) => {
  if (!terminal) return

  // Convert letter to control character
  const code = key.toUpperCase().charCodeAt(0) - 64
  terminal.paste(String.fromCharCode(code))
  terminal.focus()
}

const toggleCtrl = () => {
  ctrlPressed.value = !ctrlPressed.value
  terminal.focus()

  // Auto-release after 5 seconds
  if (ctrlPressed.value) {
    setTimeout(() => {
      ctrlPressed.value = false
    }, 5000)
  }
}

