@import url('https://fonts.googleapis.com/css2?family=JetBrains+Mono:wght@300;400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --bg-primary: #0d1117;
  --bg-secondary: #161b22;
  --bg-tertiary: #21262d;
  --border-primary: #30363d;
  --border-secondary: #21262d;
  --text-primary: #c9d1d9;
  --text-secondary: #8b949e;
  --text-tertiary: #6e7681;
  --accent-primary: #58a6ff;
  --accent-success: #3fb950;
  --accent-warning: #d29922;
  --accent-danger: #f85149;
}

/* Global styles */
body {
  font-family: 'JetBrains Mono', 'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', monospace;
  font-size: 13px;
  background: var(--bg-primary);
  color: var(--text-primary);
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Terminal styles */
.xterm {
  height: 100%;
  width: 100%;
  padding: 12px;
}

.xterm-viewport {
  width: 100% !important;
}

.xterm-screen {
  width: 100% !important;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}

::-webkit-scrollbar-track {
  background: var(--bg-secondary);
  border-radius: 0;
}

::-webkit-scrollbar-thumb {
  background: var(--border-primary);
  border-radius: 0;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--text-tertiary);
}

::-webkit-scrollbar-corner {
  background: var(--bg-secondary);
}

/* Utility classes */
.mono {
  font-family: 'JetBrains Mono', monospace;
}

.hover-bg:hover {
  background: var(--bg-tertiary);
}

/* Stat badge */
.stat-badge {
  @apply px-2 py-0.5 text-xs rounded;
  background: var(--bg-tertiary);
  color: var(--text-secondary);
  font-variant-numeric: tabular-nums;
}

/* Safe area handling for iOS devices */
.safe-area-top {
  padding-top: env(safe-area-inset-top);
}

.safe-area-bottom {
  padding-bottom: env(safe-area-inset-bottom);
}

.safe-area-left {
  padding-left: env(safe-area-inset-left);
}

.safe-area-right {
  padding-right: env(safe-area-inset-right);
}

/* PWA standalone mode adjustments */
@media all and (display-mode: standalone) {
  /* Adjust header for standalone mode */
  header {
    padding-top: env(safe-area-inset-top);
  }
}

/* Mobile-specific utilities */
@media (max-width: 768px) {
  /* Hide elements on mobile */
  .mobile-hidden {
    display: none !important;
  }

  /* Mobile font sizes */
  .mobile-text-xs {
    font-size: 0.65rem;
  }

  /* Mobile padding adjustments */
  .mobile-p-2 {
    padding: 0.5rem;
  }

  /* Touch-friendly button sizes */
  .mobile-touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Mobile terminal adjustments */
  .xterm {
    padding: 8px;
    font-size: 12px;
  }

  /* Ensure xterm terminal is visible on mobile */
  .xterm-screen {
    width: 100% !important;
    height: 100% !important;
  }

  .xterm-viewport {
    width: 100% !important;
    -webkit-overflow-scrolling: touch;
  }

  /* Mobile scrollbar (thinner) */
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  /* Mobile overlay backdrop */
  .mobile-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    z-index: 40;
  }

  /* Mobile sidebar positioning */
  .mobile-sidebar {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    z-index: 50;
  }
}

