const sendKey = (key) => {
  if (!terminal || !props.ws.isConnected.value) return

  const keyMap = {
    'Escape': '\x1b',
    'Tab': '\t',
    'ArrowUp': '\x1b[A',
    'ArrowDown': '\x1b[B',
    'ArrowLeft': '\x1b[D',
    'ArrowRight': '\x1b[C',
  }

  const data = keyMap[key] || key

  // Send through WebSocket
  props.ws.send({
    type: 'input',
    data: data
  })

  terminal.focus()
}

