// WebSocket message types
export interface BaseWebSocketMessage {
  type: string;
}

export interface ListSessionsMessage extends BaseWebSocketMessage {
  type: 'list-sessions';
}

export interface AttachSessionMessage extends BaseWebSocketMessage {
  type: 'attach-session';
  sessionName: string;
  cols: number;
  rows: number;
}

export interface InputMessage extends BaseWebSocketMessage {
  type: 'input';
  data: string;
}

export interface ResizeMessage extends BaseWebSocketMessage {
  type: 'resize';
  cols: number;
  rows: number;
}

export interface ListWindowsMessage extends BaseWebSocketMessage {
  type: 'list-windows';
  sessionName: string;
}

export interface SelectWindowMessage extends BaseWebSocketMessage {
  type: 'select-window';
  sessionName: string;
  windowIndex: number;
}

export interface PingMessage extends BaseWebSocketMessage {
  type: 'ping';
}

export type WebSocketMessage = 
  | ListSessionsMessage
  | AttachSessionMessage
  | InputMessage
  | ResizeMessage
  | ListWindowsMessage
  | SelectWindowMessage
  | PingMessage;

// WebSocket response types
export interface SessionsListResponse {
  type: 'sessions-list';
  sessions: TmuxSession[];
}

export interface OutputResponse {
  type: 'output';
  data: string;
}

export interface AttachedResponse {
  type: 'attached';
  sessionName: string;
}

export interface DisconnectedResponse {
  type: 'disconnected';
}

export interface WindowsListResponse {
  type: 'windows-list';
  windows: TmuxWindow[];
}

export interface WindowSelectedResponse {
  type: 'window-selected';
  success: boolean;
  windowIndex?: number;
  error?: string;
}

export interface PongResponse {
  type: 'pong';
}

// TMUX types
export interface TmuxSession {
  name: string;
  attached: boolean;
  created: Date;
  windows: number;
  dimensions: string;
}

export interface TmuxWindow {
  index: number;
  name: string;
  active: boolean;
  panes: number;
}

// API request/response types
export interface CreateSessionRequest {
  name?: string;
}

export interface RenameSessionRequest {
  newName: string;
}

export interface CreateWindowRequest {
  windowName?: string;
}

export interface RenameWindowRequest {
  newName: string;
}

export interface SystemStats {
  cpu: {
    cores: number;
    model: string;
    usage: number;
    loadAvg: number[];
  };
  memory: {
    total: number;
    used: number;
    free: number;
    percent: string;
  };
  uptime: number;
  hostname: string;
  platform: string;
  arch: string;
}

// node-pty types extension
import { IPty } from 'node-pty';

export interface ExtendedIPty extends IPty {
  process: string;
}
